sect1.para:
The following SQL statements will create an empty copy of the Spkdb database.
CREATE TABLE class ( class_code char(2) NOT NULL default '', class_name char(20) default NULL, parent_required tinyint(1) default '0', PRIMARY KEY (class_code) ) TYPE=MyISAM;
CREATE TABLE dataset ( dataset_id int(10) unsigned NOT NULL auto_increment, name varchar(20) NOT NULL default '', abstract varchar(100) NOT NULL default '', archive longblob, user_id int(10) unsigned NOT NULL default '0', PRIMARY KEY (dataset_id), UNIQUE KEY user_id (user_id,name) ) TYPE=InnoDB;
CREATE TABLE end ( end_code char(4) NOT NULL default '', end_name char(20) default NULL, PRIMARY KEY (end_code) ) TYPE=MyISAM;
CREATE TABLE history ( history_id int(10) unsigned NOT NULL auto_increment, event_time int(10) unsigned NOT NULL default '0', state_code varchar(4) NOT NULL default '', job_id int(10) unsigned NOT NULL default '0', host varchar(100) NOT NULL default '', PRIMARY KEY (history_id) ) TYPE=InnoDB;
CREATE TABLE job ( job_id int(10) unsigned NOT NULL auto_increment, user_id int(10) unsigned NOT NULL default '0', abstract varchar(100) NOT NULL default '', dataset_id int(10) unsigned NOT NULL default '0', dataset_version varchar(10) NOT NULL default '', model_id int(10) unsigned NOT NULL default '0', model_version varchar(10) NOT NULL default '', xml_source longblob, checkpoint longblob, state_code varchar(4) NOT NULL default '', report longblob, start_time int(10) unsigned NOT NULL default '0', event_time int(10) unsigned NOT NULL default '0', cpp_source longblob, end_code varchar(4) default NULL, method_code char(2) default NULL, parent int(10) unsigned default '0', owner varchar(40) NOT NULL default '', mail tinyint(1) default '0', PRIMARY KEY (job_id) ) TYPE=InnoDB;
CREATE TABLE method ( method_code char(2) NOT NULL default '', method_name char(20) default NULL, class_code char(2) NOT NULL default '', test_only tinyint(1) default '0', PRIMARY KEY (method_code) ) TYPE=MyISAM;
CREATE TABLE model ( model_id int(10) unsigned NOT NULL auto_increment, name varchar(20) NOT NULL default '', abstract varchar(100) NOT NULL default '', archive longblob, user_id int(10) unsigned NOT NULL default '0', PRIMARY KEY (model_id), UNIQUE KEY user_id (user_id,name) ) TYPE=InnoDB;
CREATE TABLE state ( state_code char(4) NOT NULL default '', state_name char(20) default NULL, PRIMARY KEY (state_code) ) TYPE=MyISAM;
CREATE TABLE user ( user_id int(10) unsigned NOT NULL auto_increment, first_name varchar(30) NOT NULL default '', surname varchar(40) NOT NULL default '', password varchar(32) NOT NULL default '', username varchar(20) NOT NULL default '', company varchar(30) NOT NULL default '', country varchar(20) NOT NULL default '', state varchar(20) NOT NULL default '', email varchar(40) default NULL, test tinyint(1) default '0', dev tinyint(1) default '0', PRIMARY KEY (user_id), UNIQUE KEY username (username) ) TYPE=InnoDB;

      The following SQL statements will populate static data fields in the
      database:

